mapper.update:
UPDATE item 
SET buy_count = buy_count + #{quantity}
WHERE item_code = #{item_code}

mapper.select:
SELECT COUNT(*) 
FROM buy 
WHERE item_code = #{item_code}

mapper.update:
UPDATE buy 
SET status = 'CANCELLED'
WHERE buy_code = #{value}

UPDATE buy 
SET status = 'CONFIRMED'
WHERE buy_code = #{value}
